      if (this.state.minDecible > db) {
         this.setState({
            minDecible: db
         });
      }

      // DONE
      // change maxDecible if the decible is higher than the state
      if (this.state.maxDecible < db) {
         this.setState({
            maxDecible: db
         });
      }

      // if at lowest dB then jump to the next hz
