import React, {Component} from 'react';

import Audio from './screening_routes/audio';
import Calibration from './screening_routes/calibration';
import Instructions from './screening_routes/instructions';
import Results from './screening_routes/results';

// const instructions = ({ Instructions }) => <span>{Instructions}</span>

const data = [
    {
        id: '0',
        title: 'Instructions',
        stagePercent: '0',
        component: <span>{Instructions}</span>
    },
    {
        id: '1',
        title: 'Calibration',
        stagePercent: '33',
        component: <span>{Calibration}</span>
    },
    {
        id: '2',
        title: 'Hearing Screening',
        stagePercent: '66',
        component: <span>{Audio}</span>
    },
    {
        id: '3',
        title: 'Results',
        stagePercent: '100',
        component: <span>{Results}</span>
    },
  ];

/*
    Adapted from :ReactJS: Dynamic Previous/Next Buttons
    desc: Retrieve previous & next titles and return them as component props.
    author: alpha1337
    https://codesandbox.io/s/rk360yxz?file=/index.js:3135-3772
*/
class AudioScreening extends Component {
    state = {
        stages: [],
        activeStage: '',
        modalTitle: '',
        modalPercentage: '',
        modalComponent: ''
    }

    componentDidMount() {
        this.setState({ stages: data });
    }

    handleModalOpen = (idx) => {
        this.setState({
            activeStage: idx,
            modalTitle: this.state.stages[idx].title,
            modalPercentage: this.state.stages[idx].stagePercent,
            modalComponent: this.state.stages[idx].component
        });
    };

    handleModalRestart = () => {
        this.setState({
            activeStage: ''
        });
    };

    handleNextProject = () => {

        var arr = this.state.stages.length;
        var idx = this.state.activeStage + 1;
        var idx = idx % arr;

        this.setState({
            activeStage: idx,
            modalTitle: this.state.stages[idx].title,
            modalPercentage: this.state.stages[idx].stagePercent,
            modalComponent: this.state.stages[idx].component
        });
    }

    handlePrevProject = () => {
        var arr = this.state.stages.length;
        var idx = this.state.activeStage;

        if (idx === 1) {
            this.handleModalRestart();
        } else {
            if (idx === 0) {
                var idx = arr - 1;
            } else {
                var idx = idx - 1;
            }

            this.setState({
                activeStage: idx,
                modalTitle: this.state.stages[idx].title,
                modalPercentage: this.state.stages[idx].stagePercent,
                modalComponent: this.state.stages[idx].component
            });
        }
    }

    render() {
        console.log(this.state.activeStage)

        function nextStage(idx, arr) {
            var i = idx + 1;
            var i = i % arr.length;
            return arr[i].title;
        }

        function prevStage(idx, arr) {
            if (idx === 0) {
                var i = arr.length - 1;
            } else {
                var i = idx - 1;
            }
            return arr[i].title;
        }

        if (this.state.activeStage === '') {
            return (
                <div>
                    <Breadcrumbs />
                    <Progress step="0"></Progress>
                    <h3>Click on a project below to view details.</h3>
                    <button type="button" className="btn btn-primary" 
                        onClick={this.handleModalOpen.bind(this, 1)}>  
                        Next: Calibration
                    </button>
                </div>
            );
        } else {
            return (
                <div>
                    <Breadcrumbs />
                    <Progress step={this.state.activeStage}></Progress>

                    <Modal
                        title={this.state.modalTitle}
                        stagePercent={this.state.modalPercentage}
                        previousStage={prevStage(this.state.activeStage, data)}
                        nextStage={nextStage(this.state.activeStage, data)}
                        onModalRestart={this.handleModalRestart}
                        onNext={this.handleNextProject}
                        onPrev={this.handlePrevProject}
                    />
                </div>
            );
        }
    }
}


class Modal extends React.Component {
    render() {
        return (
            <div>
                <h1>{this.props.title}</h1>
                <h3>{this.props.stagePercent}</h3>
                <button type="button" className="btn btn-primary" onClick={this.props.onPrev}>Previous: {this.props.previousStage}</button>
                <button type="button" className="btn btn-secondary" onClick={this.props.onModalRestart}>restart</button>
                <button type="button" className="btn btn-primary" onClick={this.props.onNext}>Next: {this.props.nextStage}</button>
            </div>
        );
    }
}

class Breadcrumbs extends Component {
    render() {
        return (
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb bg-transparent font-weight-light">
                    <li className="breadcrumb-item"><a className="text-danger" href="/">Home</a></li>
                    <li className="breadcrumb-item"><a className="text-danger" href="/screeninghome">Screening Info</a></li>
                    <li className="breadcrumb-item active" aria-current="page">Screening</li>
                </ol>
            </nav>
        );
    }
}

class Progress extends Component {
    render() {
        let stepSpacing = 'p-2 pl-4 ml-4'

        let step2 = ' bg-light text-muted text-nowrap'
        let step3 = ' bg-light text-muted arrow-right-inactive text-nowrap'
        let step4 = ' bg-light text-muted arrow-right-inactive text-nowrap'
        if (this.props.step >= 1) {
            step2 = ' bg-primary arrow-active text-nowrap';
        }
        if (this.props.step >= 2) {
            step3 = ' bg-primary arrow-active text-nowrap';
        }
        if (this.props.step >= 3) {
            step4 = ' bg-primary arrow-active text-nowrap';
        }

        return (
            <div className="font-weight-light text-center p-2">
                <ul className="d-inline-flex align-content-center text-dark" id="progress">
                    <li className="active">Instructions</li>
                    <li className={step2}>Calibration</li>
                    <li>Hearing Screening</li>
                    <li>Results</li>
                </ul>
            </div>
        );
    }
}

export default AudioScreening;
