import React, {Component} from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar'

import Audio from './screening_routes/audio';
import Calibration from './screening_routes/calibration';
import Instructions from './screening_routes/instructions';
import Results from './screening_routes/results';

const PAGES = [
    { id:'0', comp:{Instructions}, path: "/instructions", now:"0" },
    { id:'1', comp:{Calibration}, path: "/calibration", now:"33" },
    { id:'2', comp:{Audio}, path: "/audio", now:"66" },
    { id:'3', comp:{Results}, path: "/results", now:"100" }
];

const data = [
    {
      id: '1',
      title: 'SapientNitro',
      description: 'I used to work at SapientNitro.',
      url: 'https://placeimg.com/150/150/nature/sepia'
    },
    {
      id: '2',
      title: 'Razorfish',
      description: 'SapientNitro merged with Razorfish.',
      url: 'https://placeimg.com/150/150/animals/sepia'
    },
    {
      id: '3',
      title: 'SapientRazorfish',
      description: 'Now I work at SapientRazorfish.',
      url: 'https://placeimg.com/150/150/tech/sepia'
    },
  ];

class AudioScreening extends Component {
    state = {
        projects: [],
        activeProject: '',
        modalTitle: '',
        modalDescription: ''
      }

      componentDidMount() {
        this.setState({ projects: data });
      }

      handleModalOpen = (idx) => {
        this.setState({
          activeProject: idx,
          modalTitle: this.state.projects[idx].title,
          modalDescription: this.state.projects[idx].description
        });  
      };

      handleModalClose = () => {
        this.setState({
          activeProject: ''
        });  
      };

      handleNextProject = () => {

        var arr = this.state.projects.length;
        var idx = this.state.activeProject + 1;
        var idx = idx % arr;

        this.setState({
          activeProject: idx,
          modalTitle: this.state.projects[idx].title,
          modalDescription: this.state.projects[idx].description
        }); 
      }

      handlePrevProject = () => {
        var arr = this.state.projects.length;
        var idx = this.state.activeProject;

        console.log('initial: ' + idx);

        if (idx === 0) {
          var idx = arr - 1;
        } else {
          var idx = idx -1;
        }

        console.log('updated: ' + idx);

        this.setState({
          activeProject: idx,
          modalTitle: this.state.projects[idx].title,
          modalDescription: this.state.projects[idx].description
        });  
      }


    render() {
        // let audioTestStage = 0

        console.log(this.state)

    function nextTitle(idx, arr) {
      var i = idx + 1;
      var i = i % arr.length;
      return arr[i].title;
    }

    function prevTitle(idx, arr) {

      if (idx === 0) {
        var i = arr.length -1;
      } else {
        var i = idx -1;
      }

      return arr[i].title;
    }

    const projectComponents = data.map((data, idx, arr) =>
      <Project
        key={'project-' + data.id}
        index={idx}
        title={data.title}
        url={data.url}
        onModalOpen={this.handleModalOpen}
      />,
    );

    if(this.state.activeProject === '') {
      return (
        <div>
          <h3>Click on a project below to view details.</h3>
          {projectComponents}
        </div>
      );
    } else {
      return (
        <div>
          <Modal 
            title={this.state.modalTitle}
            description={this.state.modalDescription}
            previousTitle={prevTitle(this.state.activeProject, data)}
            nextTitle={nextTitle(this.state.activeProject, data)}
            onModalClose={this.handleModalClose}
            onNext={this.handleNextProject}
            onPrev={this.handlePrevProject}
          />
        </div>
      );
    }

        // return (
        //     <div>
        //         <nav aria-label="breadcrumb">
        //             <ol className="breadcrumb bg-transparent font-weight-light">
        //             <li className="breadcrumb-item"><a className="text-danger" href="/">Home</a></li>
        //             <li className="breadcrumb-item"><a className="text-danger" href="/screeninghome">Screening Info</a></li>
        //             <li className="breadcrumb-item active" aria-current="page">Screening</li>
        //             </ol>
        //         </nav>

        //         <Progress percentage={PAGES}></Progress>
        //         <Test pages={PAGES} testStage={audioTestStage}></Test>

        //         <div>
        //             <h2>Message :</h2>

        //             <p>{this.state.msg}</p>


        //             {/* Set click handler */}
        //             <button onClick={this.handleClick}>
        //             Click here!
        //             </button>
        //         </div>

        //     </div>
        // );
    }
}


class Modal extends React.Component {
    render() {
      return (
        <div>
          <h1>{this.props.title}</h1>
          <h3>{this.props.description}</h3>
          <button onClick={this.props.onPrev}>{'\u2B05'} {this.props.previousTitle}</button>
          <button onClick={this.props.onModalClose}>Close Modal</button>
          <button onClick={this.props.onNext}>{this.props.nextTitle} {'\u27A1'}</button>    
        </div>
      );
    }
  }

  class Project extends React.Component {
    render() {
      return (
        <div onClick={this.props.onModalOpen.bind(this, this.props.index)}>
          <img src={this.props.url} />
        </div>
      );
    }
  }


class Progress extends Component {
    render() {
        let now = this.props.percentage.map((playlistInfo) => {
            return (playlistInfo.now);
        });

        return (
            <div>
                <ProgressBar now={now} label={`${now}%`} />
            </div>
        );
    }
}

class Test extends Component {
    render() {
        // let holder = "/instructions"
        // let compholder = Instructions

        return(
            <div>
                <button type="button" className="btn btn-primary">Back</button>
                <button type="button" className="btn btn-secondary">Next</button>
                <button type="button" className="btn btn-secondary">Instructions</button>
                <button type="button" className="btn btn-secondary">Restart</button>

            </div>
        );
    }
}

export default AudioScreening;
