import React, {Component} from 'react';
import ProgressBar from 'react-bootstrap/ProgressBar'

import Audio from './screening_routes/audio';
import Calibration from './screening_routes/calibration';
import Instructions from './screening_routes/instructions';
import Results from './screening_routes/results';

const data = [
    {
      id: '1',
      title: 'Calibration',
      stagePercent: '33',
      component: {Calibration}    
    },
    {
      id: '2',
      title: 'Hearing Screening',
      stagePercent: '66',
      component: {Audio}  
    },
    {
      id: '3',
      title: 'Results',
      stagePercent: '100',
      component: {Results}  
    },
  ];

/*
    Adapted from :ReactJS: Dynamic Previous/Next Buttons
    desc: Retrieve previous & next titles and return them as component props.
    author: alpha1337
    https://codesandbox.io/s/rk360yxz?file=/index.js:3135-3772
*/
class AudioScreening extends Component {
    state = {
        stages: [],
        activeStage: '',
        modalTitle: '',
        modalPercentage: ''
    }

    componentDidMount() {
        this.setState({ stages: data });
    }

    handleModalOpen = (idx) => {
        this.setState({
            activeStage: idx,
            modalTitle: this.state.stages[idx].title,
            modalPercentage: this.state.stages[idx].stagePercent
        });
    };

    handleModalRestart = () => {
        this.setState({
            activeStage: ''
        });
    };

    handleNextProject = () => {

        var arr = this.state.stages.length;
        var idx = this.state.activeStage + 1;
        var idx = idx % arr;

        this.setState({
            activeStage: idx,
            modalTitle: this.state.stages[idx].title,
            modalPercentage: this.state.stages[idx].stagePercent
        });
    }

    handlePrevProject = () => {
        var arr = this.state.stages.length;
        var idx = this.state.activeStage;

        if (idx === 0) {
            var idx = arr - 1;
        } else {
            var idx = idx - 1;
        }

        this.setState({
            activeStage: idx,
            modalTitle: this.state.stages[idx].title,
            modalPercentage: this.state.stages[idx].stagePercent
        });
    }


    render() {
        function nextStage(idx, arr) {
            var i = idx + 1;
            var i = i % arr.length;
            return arr[i].title;
        }

        function prevStage(idx, arr) {
            if (idx === 0) {
                var i = arr.length - 1;
            } else {
                var i = idx - 1;
            }
            return arr[i].title;
        }

        const projectComponents = data.map((data, idx) =>
            <Project
                key={'project-' + data.id}
                index={idx}
                title={data.title}
                onModalOpen={this.handleModalOpen}
            />,
        );

        if (this.state.activeStage === '') {
            return (
                <div>
                    <Breadcrumbs />
                    <Progress percentage="0"></Progress>
                    <Instructions />
                    <h3>Click on a project below to view details.</h3>
                    {projectComponents}
                </div>
            );
        } else {
            return (
                <div>
                    <Breadcrumbs />
                    <Progress percentage="33"></Progress>
                    <Modal
                        title={this.state.modalTitle}
                        stagePercent={this.state.modalPercentage}
                        previousStage={prevStage(this.state.activeStage, data)}
                        nextStage={nextStage(this.state.activeStage, data)}
                        onModalRestart={this.handleModalRestart}
                        onNext={this.handleNextProject}
                        onPrev={this.handlePrevProject}
                    />
                </div>
            );
        }
    }
}


class Modal extends React.Component {
    render() {
        return (
            <div>
                <h1>{this.props.title}</h1>
                <h3>{this.props.stagePercent}</h3>
                <button type="button" className="btn btn-primary" onClick={this.props.onPrev}>back to {this.props.previousTitle}</button>
                <button type="button" className="btn btn-secondary" onClick={this.props.onModalRestart}>restart</button>
                <button type="button" className="btn btn-primary" onClick={this.props.onNext}>next</button>
            </div>
        );
    }
}

class Project extends React.Component {
    render() {
        return (
            <div onClick={this.props.onModalOpen.bind(this, this.props.index)}>
                <button type="button" className="btn btn-secondary">Next</button>
            </div>
        );
    }
}

class Breadcrumbs extends Component {
    render() {
        return (
            <nav aria-label="breadcrumb">
                <ol className="breadcrumb bg-transparent font-weight-light">
                    <li className="breadcrumb-item"><a className="text-danger" href="/">Home</a></li>
                    <li className="breadcrumb-item"><a className="text-danger" href="/screeninghome">Screening Info</a></li>
                    <li className="breadcrumb-item active" aria-current="page">Screening</li>
                </ol>
            </nav>
        );
    }
}

class Progress extends Component {
    render() {
        // let now = this.props.percentage.map((playlistInfo) => {
        //     return (playlistInfo.now);
        // });

        let now = this.props.percentage

        return (
            <div className="font-weight-light">
                <h5 className="font-weight-light mb-1">Progress</h5> 
                <ProgressBar now={now} label={`${now}%`} />
            </div>
        );
    }
}

export default AudioScreening;
