import React, { Component } from 'react';

import { TEST } from './TestConstant.js'

const pathStart = "audio_tones/";
const pathEnd = ".mp3";

class Audio extends Component {
   state = {
      test: [],

      currentEarID: 0, // 0=R starting
      currentHzID: 0, // 0=1000 starting 
      // (do not need to track dbID since changes based on the yes or no)

      activeEar: 'R',
      activeHertz: 1000, 
      activeDecible: 30, 

      maxDecible: 0 // set arbitrary low and high to show if ever heard
   }

   componentDidMount() {
      this.setState({ 
         test: TEST
      });
   }

   // use activeDecible to then pass to this function to get array id
   getDecibleID = (db) => {
      let id = (db - 15) / 5;
      return id;
   }

   // when you click yes
   handleDrop = () => {
      let earID = this.state.currentEarID;
      let hzID = this.state.currentHzID;
      let db = this.state.activeDecible;
      let dbID = this.getDecibleID(db);
      let maxDb = this.state.maxDb;

      // test object copy
      let test = [...this.state.test];

      if (this.state.maxDecible < db) { // done
         this.setState({
            maxDecible: db
         });
      }

      // check if first time hz has played
         // !yes = update ableToHear for hz level to true
      if (!test[earID].hertz[hzID].ableToHear) { 
         test[earID].hertz[hzID].ableToHear = true;
      }

      // check if this db has played before for this hz
         // !yes = set hasPlayed for decible level to true
      if (!test[earID].hertz[hzID].decibles[dbID].hasPlayed) {
         test[earID].hertz[hzID].decibles[dbID].hasPlayed = true;
      } 

      // overall things to update
         // current db numHeard +1
      test[earID].hertz[hzID].decibles[dbID].numHeard += 1;


      // check numHeard + numMissed to tell if repeat or move on
      let numHeard = test[earID].hertz[hzID].decibles[dbID].numHeard;
      let numMissed = test[earID].hertz[hzID].decibles[dbID].numMissed;
      let totalPlayed = numHeard + numMissed;
      let hitRate = numHeard / totalPlayed;

      if (totalPlayed >= 2 && hitRate > .65) {
         // check if test...minThres is larger than current dB
            // yes = set current db as minThres
         if (test[earID].hertz[hzID].minThres >= db) {
            test[earID].hertz[hzID].minThres = db;
         }
      } 

      // if at lowest dB then jump to the next hz
      if (db === 20) { // edge case
         db -= 5;
      } else if (db - 10 >= 15) { // drop case
         db -= 10;
      } else if ( totalPlayed >= 2 && hitRate > .65 ) {

         if ( earID === 0 && test[earID].hertz[hzID].hzLevel !== 500 ) { // change hz
            hzID += 1;
            db = 30;
            test[earID].hertz[hzID].minThres = db; // set min thres before moving on

            // first ear -- if second time hearing 1000hz then set dB to min threshold
            // if min threshold is not set (100), then just use 30
            if ((hzID === 4) && (test[earID].hertz[hzID].minThres < 100 && (earID !== 1))) {
               db = test[earID].hertz[0].minThres
            }         
         } else if (earID === 0 && test[earID].hertz[hzID].hzLevel === 500) {
            console.log("next ear, first hz, 30 db")
            earID = 1;
            hzID = 0;
            db = 30;
            maxDb = 0;
         } else {
            console.log("end test");
            // TO DO END THE TEST
         }
      } 

      this.setState({
         test: test,
         currentEarID: earID,
         currentHzID: hzID, 
         activeEar: this.state.test[earID].ear,
         activeHertz: this.state.test[earID].hertz[hzID].hzLevel, 
         activeDecible: db,
         maxDecible: maxDb
      }, () => console.log(this.state.test));
   };

   // when you click no
   handleIncrease = () => {
      let earID = this.state.currentEarID;
      let hzID = this.state.currentHzID;
      let db = this.state.activeDecible;
      // let dbID = this.getDecibleID(db);
      // let maxDb = this.state.maxDb;

      // test object copy
      let test = [...this.state.test];


      // if at highest dB then jump to the next hz
      if (db === 75) {

         if (hzID === 5) { // case where cannot go up any higher and cannot go to a higher hz
            console.log("now at highest db for highest hz")
         } else {
            console.log("next hz")
            hzID += 1;
            this.setState({
               currentHzID: hzID, 
               activeHertz: this.state.test[earID].hertz[hzID].hertz, 
               activeDecible: 30 // for testing purposes it is set here
            });
         }
      } else { // go up by 5db
         console.log("up by 5dB")
         db += 5;
         this.setState({
            activeDecible: db
         });
      }
   };

   // currently only restarting at 500hz and 20 db
   handleReplay = () => {
      console.log("reset test")
      this.setState({
         test: TEST,

         currentEarID: 0, // 0=R starting
         currentHzID: 0, // 0=1000 starting

         activeEar: 'R',
         activeHertz: 1000, 
         activeDecible: 30, 

         maxDecible: 0,
      });
   };

   // able to go down and manipulate the this.state.test object however deep need to
   debuggin = () => {
      // console.log(this.state.test[0].hertz[0].minThres)

      let test = [...this.state.test]; // 1. Make a shallow copy of the object (entire state object)

      test[0].hertz[0].minThres += 100; // 2. Replace the property you're intested in

      this.setState({test}, // 3. Set the state to our new copy
         () => console.log(this.state.test[0].hertz[0].minThres)
         )
   }

   render() {
      return (
         <main>
            <div className="container text-center d-flex flex-column">
               <button type="button" className="btn btn-secondary" onClick={this.debuggin}>test</button>
               <ToneDisplay 
                  currentEar={this.state.activeEar} // passing current ear
                  currentHZ={this.state.activeHertz} // passing current hz
                  currentDB={this.state.activeDecible} // passing current db
               />
               <h4 className="font-weight-light py-2 m-0">Do you hear this tone?</h4>
               <AudioResponse 
                  drop10={this.handleDrop} // passing drop function
                  increase5={this.handleIncrease} // passing increase function
                  replay={this.handleReplay} // passing replay function (currently resetting hz/db)
               />
               {/* placeholder button for now */}
               <button type="button" className="btn btn-danger" onClick={this.props.disable}>(placeholder) activate next button</button>
             </div>
         </main>
      );
   }
}

class ToneDisplay extends Component {
   render() {
      // using the passed hz/db to concat different parts together for file path name
      let ear = this.props.currentEar;
      let hz = "Hz" + this.props.currentHZ;
      let db = "dB" + this.props.currentDB;
      console.log(hz + db);

      return (
         <div>
            <h1>
               {ear + " " + hz + " " + db}
            </h1>
            <p>{pathStart + ear + hz + db + pathEnd}</p>
         </div>
      );
   }
}

class AudioResponse extends Component {
   render() {
      return (
         <div className="d-inline-flex justify-content-center">
            <div className="row py-2 mb-4 m-2 align-items-center">
               <div className="col">

                  {/* onClick= functionality traces back to main component to run function */}
                  <button type="button" className="btn btn-dark text-nowrap screening-btn-hear" onClick={this.props.increase5}> 
                     <i className="bi bi-hand-thumbs-down text-black"></i> No
                  </button>

               </div>
               <button type="button" className="btn" onClick={this.props.replay}>
                  <h3 className="mb-0"><i className="bi bi-arrow-repeat"></i></h3>
               </button>
               <div className="col">

                  <button type="button" className="btn btn-info text-nowrap screening-btn-hear" onClick={this.props.drop10}>
                     <i className="bi bi-hand-thumbs-up text-black"></i> Yes
                  </button>

               </div>
            </div>
         </div>
      );
   }
}

export default Audio;
