import React, { Component } from 'react';

const tones = [
   {
      id: '1',
      hertz: '500', //500hz
      decibles: {
         path20: "./audio_tones/Hz500dB20.png",
         path25: "./audio_tones/Hz500dB25.png",
         path30: "./audio_tones/Hz500dB30.png",
         path35: "./audio_tones/Hz500dB35.png",
         path40: "./audio_tones/Hz500dB40.png",
         path45: "./audio_tones/Hz500dB45.png",
         path50: "./audio_tones/Hz500dB50.png"
      }
   },
   {
      id: '2',
      hertz: '1000', //1000hz
      decibles: {
         path20: "./audio_tones/Hz1000dB20.png",
         path25: "./audio_tones/Hz1000dB25.png",
         path30: "./audio_tones/Hz1000dB30.png",
         path35: "./audio_tones/Hz1000dB35.png",
         path40: "./audio_tones/Hz1000dB40.png",
         path45: "./audio_tones/Hz1000dB45.png",
         path50: "./audio_tones/Hz1000dB50.png"
      }
   },
];

const path = "audio_tones/Hz1000dB20.png";

class Audio extends Component {
   state = {
      tones: [],
      activeHertz: '',
      activeDecible: ''
   }

   componentDidMount() {
      this.setState({ tones: tones });
   }

   handleDrop = () => {
      var arr = this.state.stages.length;
      var idx = this.state.activeStage;

      if (idx === 1) {
         this.handleModalRestart();
      } else {
         if (idx === 0) {
            var idx = arr - 1;
         } else {
            var idx = idx - 1;
         }

         this.setState({
            activeStage: idx,
            modalTitle: this.state.stages[idx].title,
            modalPercentage: this.state.stages[idx].stagePercent
         });
      }
   }

   handleIncrease = () => {
      console.log("up")
   }

   handleReplay = () => {
      console.log("replay")
   }

   render() {
      return (
         <main>
            <div className="container text-center">
               <ToneDisplay 
                  hz={this.state.activeHertz}
                  db={this.state.activeDecible}
               />
               <h4 className="font-weight-light py-2 m-0">Do you hear this tone?</h4>
               <AudioResponse 
                  drop10={this.handleDrop}
                  increase5={this.handleIncrease}
                  replay={this.handleReplay}
               />
             </div>
         </main>
      );
   }
}

class ToneDisplay extends Component {
   render() {
      return (
         <div>
            <img src={path} alt="Logo" />
         </div>
      );
   }
}

class AudioResponse extends Component {
   render() {
      return (
         <div className="d-inline-flex justify-content-center">
            <div className="row py-2 mb-4 m-2 align-items-center">
               <div className="col">

                  <button type="button" className="btn btn-dark text-nowrap screening-btn-hear" onClick={this.props.drop10}>
                     <i className="bi bi-hand-thumbs-down text-black"></i> No
                  </button>

               </div>
               <button type="button" className="btn" onClick={this.props.replay}>
                  <h3 className="mb-0"><i className="bi bi-arrow-repeat"></i></h3>
               </button>
               <div className="col">

                  <button type="button" className="btn btn-info text-nowrap screening-btn-hear" onClick={this.props.increase5}>
                     <i className="bi bi-hand-thumbs-up text-black"></i> Yes
                  </button>

               </div>
            </div>
         </div>
      );
   }
}

